    ldr  r0,[r0,#TPL_KERN_OFFSET_S_RUNNING]
    bl   tpl_load_context_under_it

tpl_SysTick_no_context_switch:

    /*-------------------------------------------------------------------------
     * 8 - pop LR that was saved at stage 1 into PC and return from the handler
